INSERT INTO `activities` VALUES ('82', '1', '41120-1067', '2010-11-01', '2012-12-31', 'USD', 'Disaster prevention and preparedness', 'Support To Living With Floods In Chikwawa District, Lower Shire Valley, Malawi', 'The overall goal is to reducevulnerability to floods of communities living in low lands prone to low and moderate flooding byreinforcing local capacities and applying sustainable coping solutions through innovative small-scale mitigationinterventions for floods to support the alternative strategy of Living with Floods rather thanrelocation.');
